import React from 'react'
import './User.css'
import {useContext} from 'react'
import {appContext} from './Context'
import {useParams} from 'react-router-dom';

function User() {
  const {list,setList,name,setName,course,setCourse}=useContext(appContext)

  let { id } = useParams();
  if(id!==undefined && id===null){
    const item=list.filter(x => (x.id===parseInt(id)));
    setName(item[0].name)
    setCourse(item[0].course)
  }
  const handleSubmit=(e)=>{
    e.preventDefault();
    if(id!==undefined && id===null){
      const newList= list.map((item)=>(
        (item.id === id)? {...item,name,course}:item  
      ))
      setList(newList)
    }
    else{
      const length=list.length
      const newItem={id:{length},name,course}
      setList([...list,newItem])
    }
    
    }

  return (
   <div>
     <h1>USER details</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" value={name} onChange={(e)=>setName(e.target.value)} placeholder="Name" required /><br/>
        <input type="text" value={course} onChange={(e)=>setCourse(e.target.value)} placeholder="Course" required />
        <button> submit </button>
      </form>
    </div>
    
  )
}

export default User
