import React from 'react'
import './User.css'
import {useContext} from 'react'
import {appContext} from './Context'
import {useParams, useNavigate} from 'react-router-dom';

function User() {
  const {list,setList,name,setName,course,setCourse}=useContext(appContext)
  const navigate=useNavigate();

  const { id } = useParams();
  const item={name:""}
  
  if(id!==undefined && id!==null){
    const item=list.filter(x => (x.id===parseInt(id)));
    setName(item[0].name)
    setCourse(item[0].course)
  }
  const handleSubmit=(e)=>{
    e.preventDefault();
    if(id!==undefined && id!==null){
      const newList= list.map((item)=>(
        (item.id === id)? {...item,name,course}:item  
      ))     
      setList(newList)
      alert(id)
      setName("")
    setCourse("")
    navigate('/');
    }
    else{
      const length=list.length+1
      
      const newItem={id:length,name,course}
      setList([...list,newItem])
      
      setName("")
      setCourse("")
      navigate('/');
    }
      }
    const handleName=(e)=>{
      setName(e.target.value)
  
      }
      const handleCourse=(e)=>{
      setCourse(e.target.value)
  
      }  

  return (
   <div>
     <h1>USER details</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" value={item[0].name} onChangeCapture={handleName} placeholder="Name" required /><br/>
        <input type="text" value={item[0].course} onChangeCapture={handleCourse} placeholder="Course" required />
        <button> submit </button>
      </form>
    </div>
    
  )
}

export default User
